const report = $input.first().json.output;
const timestamp = new Date().toISOString();

// Format for email (HTML)
const emailHtml = `
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    h1 { color: #2c3e50; border-bottom: 3px solid #3498db; padding-bottom: 10px; }
    h2 { color: #34495e; margin-top: 25px; }
    .metric { background: #ecf0f1; padding: 10px; margin: 10px 0; border-radius: 5px; }
    .high-priority { background: #ffe6e6; border-left: 4px solid #e74c3c; padding: 10px; }
    .opportunity { background: #e8f8f5; border-left: 4px solid #27ae60; padding: 10px; }
    ul { margin: 10px 0; }
    .footer { margin-top: 30px; padding-top: 20px; border-top: 1px solid #bdc3c7; font-size: 0.9em; color: #7f8c8d; }
  </style>
</head>
<body>
  <h1>üåç Global Market Intelligence Report</h1>
  <p><strong>Generated:</strong> ${new Date(report.reportDate || timestamp).toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</p>

  <h2>üìä Market Sentiment Overview</h2>
  <div class="metric">
    <strong>Overall Sentiment:</strong> ${report.marketSentiment?.overall || 'N/A'} (Score: ${report.marketSentiment?.score || 'N/A'}/10)<br>
    <strong>Key Drivers:</strong> ${(report.marketSentiment?.drivers || []).join(', ')}
  </div>

  <h2>üöÄ Emerging Trends</h2>
  ${(report.emergingTrends || []).map(trend => `
    <div class="metric">
      <strong>${trend.trend}</strong> (Confidence: ${trend.confidence}, Impact: ${trend.impactScore}/10)<br>
      ${trend.description}<br>
      <em>Sources: ${trend.sources.join(', ')}</em>
    </div>
  `).join('')}

  <h2>‚ö†Ô∏è Crisis Signals</h2>
  ${(report.crisisSignals || []).map(signal => `
    <div class="high-priority">
      <strong>${signal.signal}</strong><br>
      Severity: ${signal.severity} | Probability: ${(signal.probability * 100).toFixed(0)}% | Timeframe: ${signal.timeframe}<br>
      <strong>Mitigation:</strong> ${signal.mitigation}
    </div>
  `).join('')}

  <h2>üí° Investment Opportunities</h2>
  ${(report.investmentOpportunities || []).map(opp => `
    <div class="opportunity">
      <strong>${opp.opportunity}</strong> (${opp.type})<br>
      ${opp.rationale}<br>
      Risk: ${opp.riskLevel} | Horizon: ${opp.timeHorizon} | Potential Return: ${opp.potentialReturn}
    </div>
  `).join('')}

  <h2>üåê Geopolitical Risks</h2>
  ${(report.geopoliticalRisks || []).map(risk => `
    <div class="metric">
      <strong>${risk.risk}</strong> (Probability: ${(risk.probability * 100).toFixed(0)}%)<br>
      Impacted Sectors: ${risk.impactedSectors.join(', ')}<br>
      ${risk.marketImpact}
    </div>
  `).join('')}

  <h2>üéØ Actionable Recommendations</h2>
  <ul>
    ${(report.actionableRecommendations || []).map(rec => `<li>${rec}</li>`).join('')}
  </ul>

  <h2>üí° Key Insights</h2>
  <ul>
    ${(report.keyInsights || []).map(insight => `<li>${insight}</li>`).join('')}
  </ul>

  <div class="footer">
    <p>This report was automatically generated by the Autonomous Global Market Intelligence System.<br>
    Data sources: Financial News APIs, Global News RSS, Reddit, Twitter/X, Market Data APIs</p>
  </div>
</body>
</html>
`;

// Format for Slack (Markdown)
const slackMessage = `*üåç Global Market Intelligence Report*
_Generated: ${new Date(report.reportDate || timestamp).toLocaleDateString()}_

*üìä Market Sentiment:* ${report.marketSentiment?.overall || 'N/A'} (${report.marketSentiment?.score || 'N/A'}/10)

*üöÄ Top Emerging Trends:*
${(report.emergingTrends || []).slice(0, 3).map(t => `‚Ä¢ *${t.trend}* (Impact: ${t.impactScore}/10)\n  ${t.description}`).join('\n')}

*‚ö†Ô∏è Crisis Signals:*
${(report.crisisSignals || []).map(s => `‚Ä¢ *${s.signal}* - ${s.severity} severity (${(s.probability * 100).toFixed(0)}% probability)`).join('\n')}

*üí° Top Opportunities:*
${(report.investmentOpportunities || []).slice(0, 3).map(o => `‚Ä¢ *${o.opportunity}* - ${o.potentialReturn} potential return`).join('\n')}

*üéØ Key Recommendations:*
${(report.actionableRecommendations || []).slice(0, 5).map(r => `‚Ä¢ ${r}`).join('\n')}

_Full report available in email and dashboard_`;

// Format for Google Sheets
const sheetRow = {
  timestamp: timestamp,
  reportDate: report.reportDate || timestamp,
  overallSentiment: report.marketSentiment?.overall || '',
  sentimentScore: report.marketSentiment?.score || 0,
  topTrend: (report.emergingTrends || [])[0]?.trend || '',
  trendImpact: (report.emergingTrends || [])[0]?.impactScore || 0,
  crisisCount: (report.crisisSignals || []).length,
  topCrisis: (report.crisisSignals || [])[0]?.signal || '',
  opportunityCount: (report.investmentOpportunities || []).length,
  topOpportunity: (report.investmentOpportunities || [])[0]?.opportunity || '',
  geopoliticalRiskCount: (report.geopoliticalRisks || []).length,
  keyInsights: (report.keyInsights || []).join(' | '),
  fullReportJson: JSON.stringify(report)
};

return [
  {
    json: {
      emailHtml,
      slackMessage,
      sheetRow,
      originalReport: report
    }
  }
];

const sources = $input.first().json.allDataSources  || [];
let totalCount = 0;

const formattedSections = sources
  .map((src, i) => {
    const articles = src.articles || src.data || src.items || src.posts || src.tweets || src.feed || src.results || [];
    const count = Array.isArray(articles) ? articles.length : 0;
    if (count === 0) return null;

    totalCount += count;

    const name = src.source?.name || src.source || src.provider?.name || src.channel || src.platform || src.apiName || `Source ${i + 1}`;

    const samples = articles.map((a, j) => {
      const title = a.title || a.headline || a.name || a.text || a.content || 'Untitled';
      const desc = a.description || a.summary || a.snippet || a.body || '';
      const date = a.publishedAt || a.date || a.created_at || a.timestamp || '';
      const sourceName = a.source?.name || a.source || a.platform || `Item ${j + 1}`;
      return `   - ${title} (${sourceName}, ${date})\n     ${desc}`;
    });

    return `üìä ${name}\nTotal Items: ${count}\nSample Data:\n${samples.join('\n')}`;
  })
  .filter(Boolean);

const output = `üìà Total Combined Articles: ${totalCount}\n\n${formattedSections.join('\n\n')}`;

return [{ json: { formattedSources: output } }];
